import React, {Component} from 'react';
import classes from './MoviePosterInfo.module.css'

class MoviePosterInfo extends Component {

    render(){
        let content = null;

        if (this.props.id)
        {
            content = (
                <div className={classes.MoviePosterInfo} onClick={props.clicked}>
                    <img className={classes.img} src={'http://image.tmdb.org/t/p/w185/' + props.src} alt={props.title}/>
                    <h2>{props.title}</h2>
                </div>
            )
        }
        return(
            {content}
        );
    }
};

export default MoviePosterInfo;
