import React, {Component} from 'react';
import classes from './MoviePosterInfo.module.css'
import axios from 'axios';

class MoviePosterInfo extends Component {
    componentDidUpdate(){
        if (this.props.id){
            axios.get('http://api.themoviedb.org/3/movie/popular?api_key=0233eeb82ef3714df67f7e2db8e6ea28/' + this.props.id)
        .then(response => {
            this.setState({movies: response.data.results});
            console.log(response)
        });
        }
    }
    render(){
        let content = null;

        if (this.props.id)
        {
            content = (
                <div className={classes.MoviePosterInfo}>
                    Hello
                </div>
            )
        }
        return content;
    }
};

export default MoviePosterInfo;
