import React from 'react';
import classes from './Toolbar.module.css';
import {Link} from 'react-router-dom';
//import { Dropdown } from 'react-bootstrap';


const toolbar = props => {
    const attachedClasses = [classes.Toolbar, classes.NoButton];
    let content = (
        <div>
        <div className={attachedClasses.join(' ')}>
            <strong>{props.title}</strong> 
            <nav>
                    <ul>
                        <li><Link to='/popular' exact>Popular</Link></li>
                        <li><Link to='/top_rated' exact>Top Rated</Link></li>
                        <li><Link to='/now_playing' exact>Now Playing</Link></li>
                    </ul>
            </nav>
            <Organisms.InputDropdown
                            options={[
                                { value: 1, label: "one" },
                                { value: 2, label: "two" },
                                { value: 3, label: "three" },
                            ]}
                            name="foo"
                            onChangeHandler={() => alert('This dropdown needs to be controlled with state')}/>

        </div>


        </div>
    );
    if (props.back){
        content = (
            <div className={classes.Toolbar}>
                <strong>{props.title}</strong>

                <nav>
                    <ul>
                        <li><Link to='/popular' exact>Back</Link></li>
                    </ul>
                </nav>

            </div>
        );
    }
    return content;

};

export default toolbar;
