import React, {Component} from 'react';
import MoviePoster from '../MoviePosters/MovePoster/MoviePoster';
import MoviePosters from '../MoviePosters/MoviePosters';
import classes from './Layout.module.css';
import axios from 'axios';

class Layout extends Component {
    state = {
        movies: [],
        selectedId: null
    }

    componentDidMount(){
        axios.get('http://api.themoviedb.org/3/movie/popular?api_key=0233eeb82ef3714df67f7e2db8e6ea28')
        .then(response => {
            this.setState({movies: response.data.results});
            console.log(response)
        });
    }

    movieSelectedHandler = (id) => {
        console.log(this.state.movies.id);
        this.setState({selectedId: id});
    }

    render(){
        return (
            <div>
                <div className={classes.Toolbar}><strong>Latest Movies</strong></div>
                <MoviePosters movies={this.state.movies} selectedHandler={this.movieSelectedHandler}/>

            </div>
        );
    }    
};

export default Layout;
