import React, {Component} from 'react';
import classes from './MoviePosterInfo.module.css'
import axios from 'axios';

class MoviePosterInfo extends Component {
    state = {
        loadedMovie: null
    }
    componentDidUpdate(){
        if (this.props.id){
            //new post
            if (!this.state.loadedMovie || (this.state.loadedMovie && this.state.loadedMovie.id != this.props.id)) {
                axios.get('https://api.themoviedb.org/3/movie/' + this.props.id + '?api_key=0233eeb82ef3714df67f7e2db8e6ea28')
                .then(response => {
                    console.log(response.data)
                    this.setState({loadedMovie: response.data});
                });
            }

        }
    }
    render(){
        let content = null;

        if (this.props.id){
            content = null;
        }
        //check due to asynchronous nature
        if (this.state.loadedMovie)
        {
            content = (
                <div className={classes.MoviePosterInfo}>
                    <img 
                        className={classes.img} 
                        src={'http://image.tmdb.org/t/p/w185/' + this.state.loadedMovie.poster_path} 
                        alt={this.state.loadedMovie.title}/>
                    <h1>{this.state.loadedMovie.title}</h1>   
                    <p>Release Date: {this.state.loadedMovie.release_date}</p> 
                    <p>Plot Synopsis: {this.state.loadedMovie.overview}</p> 
                    <p>Rating: {this.state.loadedMovie.vote_average + '/10'}</p> 
                </div>
            );
        }
        return content;
    }
};

export default MoviePosterInfo;
